<?phpdefined('_JEXEC') or die; jimport('joomla.form.helper');JFormHelper::loadFieldClass('list'); /*	 ласс формирует набор значений параметра меню		 ласс св€зан с конкретным параметром посредством пол€ $type, которому должен соответствовать	тег type в конфигураторе меню в шаблоне (tmpl/default.xml). Ќи им€ файла, ни им€ класса не играют роли.	“ак что можно давать "говор€щие" имена.	≈сли в директории находитс€ только один файл, то и поле $type не играет определ€ющей роли*/class FieldIDSearchBMenu extends JFormFieldList{	protected $type = 'SearchB'; 	/**	 * Method to get a list of options for a list input.	 *	 * @return	array		An array of JHtml options.	 */	protected function getOptions() 	{		$db = JFactory::getDBO();		$query = $db->getQuery(true);		$query->select('id,name');		$query->from('#__MpzSearch');		$db->setQuery((string)$query);		$messages = $db->loadObjectList();		$options = array();		if ($messages)		{			foreach($messages as $message) 			{				$options[] = JHtml::_('select.option', $message->id, $message->name);			}		}		$options = array_merge(parent::getOptions(), $options);		return $options;	}}